import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Icon } from 'antd';
import Slick from 'react-slick';
import styled from 'styled-components';

const Overlay = styled.div`
  position: fixed;
  z-index: 5000;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
`;

const H1 = styled.h1`
`;

const Header = styled.header`
  height: 44px; 
  background: white; 
  position: relative; 
  padding: 0; 
  text-align: center;
  
  & h1 { // & : 자기 자신을 가리키는 태그
    margin: 0;
    fontsize: 17px;
    color: #333; 
    line-height: 44px;
  }
`;

const Slickwrapper = styled.div`
  height: calc(100% - 44px;
  background: #090909;
`;

const CloseBtn = styled(Icon)`
  position: absolute; 
  right: 0; 
  top: 0; 
  padding: 15px; 
  line-height: 14px; 
  cursor: pointer;
`;

const Indicator = styled.div`
  text-align: center;
  
  & > div {
    width: 75px; 
    height: 30px; 
    line-height: 30px; 
    border-radius: 15px; 
    background: #313131; 
    display: inline-block; 
    text-align: center; 
    color: white; 
    font-size: 15px;
  }
`;

const ImageWrapper = style.div`
  padding: 32px; 
  text-align: center;
  
  & img {
    margin: '0 auto;
    max-height: 750px;
  }
`;

const ImagesZoom = ({ images, onClose }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  return (
    <Overlay>
      <Header>
        <h1>상세이미지</h1>
        <CloseBtn type="close" onClick={onClose} />
      </Header>
      <Slickwrapper>
        <div>
          <Slick
            initialSlide={0}
            afterChange={slide => setCurrentSlide(slide)}
            infinite={false}
            arrows
            slidesToShow={1}
            slidesToScroll={1}
          >
            {images.map(v => {
              return (
                <ImageWrapper>
                  <img src={`http://localhost:3065/${v.src}`}/>
                </ImageWrapper>
              );
            })}
          </Slick>
            <Indicator style={{ textAlign: 'center' }}>
              <div>
                {currentSlide + 1} / {images.length}
              </div>
            </Indicator>
        </div>
      </Slickwrapper>
    </Overlay>
  );
};


ImagesZoom.propTypes = {
  images: PropTypes.arrayOf(PropTypes.shape({
    src: PropTypes.string,
  })).isRequired,
  onClose: PropTypes.func.isRequired,
};

export default ImagesZoom;
